package week1.day2;

public class IsPrime {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//input number is n
		int n=9 , i;
		
		
		
		
		for(i=2;i<n;i++) 
		{
			 if(n%i==0)
			 {
				 System.out.println("The number is nonprime");
				 break;
				 
			 }
		  
		  
		  else
		  {
			  System.out.println("The number is prime");
			  break;
		}
		
		
			
			 
		}
		
		
			 
			 
	}
}


